var ControllerBase = {

	View : function(param1, param2){
		var viewName = param2 !== undefined ? param1 : this.context.name + "/" + this.context.action;
		var model = param2 !== undefined ? param2 : param1;
		this.context.res.render(viewName, {model : model});
	}

}

module.exports.base = ControllerBase;